import requests
import time

token = 'YOUR_JWT_TOKEN'
transcription_id = '123e4567-e89b-12d3-a456-426614174000'

def get_transcription_status(transcription_id, token):
    res = requests.get(
        f'https://api.staging.commure.com/ambient/scribe/v1/transcribe/{transcription_id}',
        headers={'Authorization': f'Bearer {token}'}
    )
    return res.json()

# Get initial status
