# Poll until completed or error
while transcription['status'] in ['DETECTED_FILE', 'STT_PROCESSING']:
    print(f"Status: {transcription['status']} - waiting...")
    time.sleep(5)  # Wait 5 seconds
    transcription = get_transcription_status(transcription_id, token)

if transcription['status'] == 'STT_COMPLETED':
    print(f"Transcription completed!")
    print(f"Text: {transcription['raw_text']}")
else:
    print(f"Transcription failed with status: {transcription['status']}")

