import requests
import time

token = 'YOUR_JWT_TOKEN'
scribe_id = '123e4567-e89b-12d3-a456-426614174000'

def get_scribe_status(scribe_id, token):
    res = requests.get(
        f'https://api.staging.commure.com/ambient/scribe/v1/scribe/{scribe_id}',
        headers={'Authorization': f'Bearer {token}'}
    )
    return res.json()

# Get initial status
