    scribe = get_scribe_status(scribe_id, token)

if scribe['status'] == 'COMPLETED':
    print("\nWorkflow completed!")
    for note in scribe['ai_tasks']:
        print(f"\nNote from template {note['template_id']}:")
        print(note['note_text'])
else:
    print(f"\nWorkflow failed: {scribe['error_message']}")

